import { Component } from '@angular/core';

@Component({
  selector: 'app-switch-examp',
  templateUrl: './switch-examp.component.html',
  styleUrls: ['./switch-examp.component.css']
})
export class SwitchExampComponent {
     atm:string;
     constructor(){
      this.atm = 'Withdrawl';
     }
}

<h1>HDFC Bank EPIP</h1>

<div [ngSwitch]="atm">
    <div *ngSwitchCase="balance Check">My account balance is 10000</div>
    <div *ngSwitchCase="Withdrawl">I need to Withdrawl 5000</div>
    <div *ngSwitchCase="Deposit ">I need to deposite a 5000 amount</div>
    <div *ngSwitchDefault>Please select a options</div>
</div>
