import { Component } from '@angular/core';

@Component({
  selector: 'app-for',
  templateUrl: './for.component.html',
  styleUrls: ['./for.component.css']
})
export class ForComponent {
  cources:string[];
  students:any[];
  constructor(){
    this.cources = ['java', 'angular', 'bootstrap', 'spring']
    this.students = [
      {
        id:'1',
        name:'varun',
        course:'java',
        testScore:'91'
      },
      {
        id:'2',
        name:'prashnth',
        course:'MySQL',
        testScore:'90'
      },
      {
        id:'3',
        name:'anil',
        course:'spring',
        testScore:'95'
      },
    ];
  }
}
<h1>For Loop!!</h1>

<div *ngFor="let course of cources">
    <b></b>
    <h2>{{course}}</h2>
</div>

<table style="width:50%">
    <thead>
        <tr>
            <td>Student Id</td>
            <td>Student Name</td>
            <td>Student Cource</td>
            <td>Student TestScore</td>
        </tr>
    </thead>
    <tr *ngFor="let student of students">
        <td>{{student.id}}</td> 
        <td>{{student.name}}</td>
        <td>{{student.course}}</td>
        <td>{{student.testScore}}</td>       
    </tr>
</table>
