import { Component } from '@angular/core';

@Component({
  selector: 'app-for',
  templateUrl: './for.component.html',
  styleUrls: ['./for.component.css']
})
export class ForComponent {
  cources:string[];
  students:any[];
  color:string;
  fontSize:string;

  constructor(){
    this.cources = ['java', 'angular', 'bootstrap', 'spring']
    this.students = [
      {
        id:'1',
        name:'varun',
        course:'java',
        testScore:'91'
      },
      {
        id:'2',
        name:'prashnth',
        course:'MySQL',
        testScore:'90'
      },
      {
        id:'3',
        name:'anil',
        course:'spring',
        testScore:'95'
      },
    ];
    this.color = 'yellow';
    this.fontSize = "40";
  }
}
<h1 [style.background-color]="'blue'">For Loop!!</h1>

<div *ngFor="let course of cources">
    <b>{{course}}</b>
    <div ngNonBindable>The code render the above is {{course}}</div>
    <h2>{{course}}</h2>
</div>

<table style="width:50%">
    <thead>
        <tr>
            <td [ngStyle]="{color:color,'background-color':'blue'}">Student Id</td>
            <td>Student Name</td>
            <td>Student Cource</td>
            <td>Student TestScore</td>
        </tr>
    </thead>
    <tr *ngFor="let student of students">
        <td [style.color]="color">{{student.id}}</td> 
        <td [style.color]="color" [style.font-size.px]="fontSize">{{student.name}}</td>
        <td [style.color]="'green'" [ngClass]>{{student.course}}</td>
        <td [style.background-color]="'red'" [style.font-size.px]="fontSize">{{student.testScore}}</td>       
    </tr>
</table>
