import { Component } from '@angular/core';

@Component({
  selector: 'app-switch-examp',
  templateUrl: './switch-examp.component.html',
  styleUrls: ['./switch-examp.component.css']
})
export class SwitchExampComponent {
     atm:string;
     constructor(){
      this.atm = 'balance c';
     }
}

<h1>HDFC Bank EPIP</h1>

<ul [ngSwitch]="atm">
    <li *ngSwitchCase="'Withdrawl'"> Withdrawl</li>

    <li *ngSwitchCase="'Deposit'"> Deposit</li>

    <li *ngSwitchCase="'BalanceCheck'"> Balance Check</li>

    <li *ngSwitchDefault> Invalid choice</li>

    </ul>
